      <p>©️ 2023 Inke Launi | Suunnitellut ja kehittänyt
      <a className="link" href={pedroSite}
      target="_blank" rel="noreferrer"> Bergamo Creations</a></p>
      </Col>
      </Row>
    </div>
  );
}
}
